import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";


 const SearchBar = () => {
    const [allData, setAllData] = useState([]);
  const [filteredData, setFilteredData] = useState(allData);

  useEffect(() => {
    axios("https://fakestoreapi.com/products")
      .then((response) => {
        console.log(response.data);
        setAllData(response.data);
        setFilteredData(response.data);
      })
      .catch((error) => {
        console.log("Error getting fake data: " + error);
      });
  }, []);

  const handleSearch = (event) => {   
    let value = event.target.value.toLowerCase();
    let result = [];
    console.log(value);
    result = allData.filter((data) => {
      return data.title.search(value) !== -1;
    });
    setFilteredData(result);
  };

  return (
    <div>
        <form className="d-flex me-5 w-50">
              <input
                className="form-control me-2 w-75"
                type="text"
                placeholder="Search"
                aria-label="Search"
                onChange={(event) => handleSearch(event)}
              />
              <button className="btn btn-outline-success w-10" type="submit">
                Search
              </button>
            </form>
            <div>
        {filteredData.map((value) => {
          return (
            
            <div id="search" class="list-group" key={value.id} style={{width:"500px",margin:"0 auto"}}>
            <Link href="//products/:id" class="list-group-item list-group-item-action list-group-item-light">{value.title}</Link>
          </div>
          );
        })}
      </div>

    </div>
  )
}
export default SearchBar
