import React, { useEffect, useState } from "react";

import { Link } from "react-router-dom";

const Products = () => {
  const [data, setdata] = useState([]);
  const [filter, setFilter] = useState(data);
  const [loading, setLoading] = useState(false);
 

  let componentMounted = true;

  useEffect(() => {
    const getProduct = async () => {
      setLoading(true);
      const response = await fetch("https://fakestoreapi.com/products");
      if (componentMounted) {
        setdata(await response.clone().json());

        setFilter(await response.json());

        setLoading(false);
      }
      return () => {
        componentMounted = false;
      };
    };
    getProduct();
  }, []);

  const Loading = () => {
    return (
      <>
        <div className="col-md-3">
         <h1>
         Loading.....
         </h1>
        </div>
      </>
    );
  };

  const filterProduct = (cat)=>{
        const updateList = data.filter((x)=>x.category===cat)
        setFilter(updateList)
  }

  const ShowProducts = () => {
    return (
      < div className="row">
        <div  className="buttons mb-5 col-3">
          <li className="btn btn-outline-dark me-2" onClick={()=>setFilter(data)}>ALL</li>
          <li className="btn btn-outline-dark me-2" onClick={()=>filterProduct("men's clothing")}>Men's Clothing</li>
          <li className="btn btn-outline-dark me-2" onClick={()=>filterProduct("women's clothing")}>
            Women's Clothing
          </li>
          <li className="btn btn-outline-dark me-2" onClick={()=>filterProduct("jewelery")}>Jewelery</li>
          <li className="btn btn-outline-dark me-2" onClick={()=>filterProduct("electronics")}>Electronic</li>
        </div>
        <div className="col-md-8">
        {filter.map((product) => {
          return (
            <>
              <div className="row mb-4 ">
                <div className="card h-100 text-center p4 col" key={product.id}>
                  <img
                    src={product.image}
                    className="card-img-top"
                    alt={product.title}
                    height="250px"
                  />
                  <div className="card-body" >
                    <h5 className="card-title mb-0">
                      {product.title.substring(0, 12)}...
                    </h5>
                    <p className="card-text lead fw-bold">${product.price}</p>
                    <Link to={`/products/${product.id}`} className="btn btn-primary">
                      Buy Now
                    </Link>
                  </div>
                </div>
              </div>
            </>
          );
        })}
        </div>
      </div>
    );
  };

  return (
    <div>
      <div className="container my-5 py-5">
        <div className="row">
          <div className="col-12 mb-5">
            <h1 className="display-6 fw-bolder text-center">Latest Product</h1>
            <hr />
          </div>
        </div>
        <div className="row justify-content-center">
          {loading ? <Loading /> : <ShowProducts />}
        </div>
      </div>
    </div>
  );
};

export default Products;
